move '(' = 1
move ')' = -1

climb (step, floor) change
 | floor == -1 = (step, floor)
 | change == '(' = (step + 1, floor + 1)
 | change == ')' = (step + 1, floor -1)
 | otherwise = (step, floor)

main = do
  putStrLn "Santa will end up at floor:"
  print $ sum(map move input)
  print $ foldl climb (0, 0) input
